mapper.select:
select * from ( select b.system_ID system_Id, b.system_Name,a.APP_NAME,a.APP_INSTANCE_NAME,a.CONFIG_KEY,a.CONFIG_VALUE,a.CONFIG_TYPE,a.CONFIG_PATH,a.CONFIG_SEQ from (select b.APP_NAME,a.DICT_NAME CONFIG_TYPE,b.CONFIG_SEQ ,b.CONFIG_TYPE dictfile,b.CONFIG_VALUE, b.APP_INSTANCE_NAME , b.CONFIG_KEY,b.CONFIG_PATH from BBF_APP_CONFIG b, BBF_DICT a where a.DICT_ID = b.CONFIG_TYPE) a , (select b.SYSTEM_ID system_ID,a.DICT_NAME SYSTEM_Name,b.CONFIG_SEQ from BBF_APP_CONFIG b ,BBF_DICT a where a.DICT_ID = b.SYSTEM_ID) b where a.CONFIG_SEQ =b.CONFIG_SEQ) where 1=1
AND SYSTEM_Name = #{systemName}
AND APP_NAME LIKE '%'||#{appName}||'%' ESCAPE '/'
AND APP_INSTANCE_NAME = #{appInstanceName}
