mapper.select:
select 
  FROM O_ADMIN
  where ACCOUNT=#{account} and password=#{password}

mapper.insert:
insert into O_ADMIN (ID, ACCOUNT, PASSWORD,
  NAME, PHONE, EMAIL,
  STATUS, CREATETIME, SEX, 
  ROLEID, HEAD_PIC, OPERATOR
  )
values (seq_O_admin.nextval, #{account,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR},
  #{name,jdbcType=VARCHAR}, #{phone,jdbcType=VARCHAR}, #{email,jdbcType=VARCHAR}, 
  #{status,jdbcType=CHAR}, #{createtime,jdbcType=TIMESTAMP}, #{sex,jdbcType=CHAR}, 
  #{roleid,jdbcType=DECIMAL}, #{headPic,jdbcType=VARCHAR}, #{operator,jdbcType=DECIMAL}
  )
